template <>
inline bool pandora::PandoraInputType<arbor_content::SemiDigitalThresholdList>::IsValid(const arbor_content::SemiDigitalThresholdList &list) const
{
	if(list.empty())
		return false;

	for(unsigned int i=0 ; i<list.size() ; i++)
	{
		if( !(IS_NAN(static_cast<unsigned int>(list.at(i))) || IS_INF(static_cast<unsigned int>(list.at(i)))) )
			return false;
	}

	return true;
}

